package appiumtests;

public class inc {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 100;
		System.out.println(i++);
		System.out.println(i);
		int y = 7;
		System.out.println(++y * 8);
		
		int j = 100;
		System.out.println(++j);
		System.out.println(j);
		
		int k = 100;
		System.out.println(--k);
		System.out.println(k);
		
		int l = 100;
		System.out.println(l--);
		System.out.println(l);
	}

}
